RandomPlatform:
	# Parameters: None
	# Return : pixel value
	
	lw $t0, displayAddress 		# $t0 stores the base address for display
	
	# horizontal coordinate value
	li $v0, 42
	li $a0, 0
	li $a1, 40
	syscall
	
	add $t3, $zero, $a0 		# updated t3 with horizontal random value and t3 = t0 + a0 (aligning with the address)
	addi $t1, $zero, 4
	mult $t3, $t1
	mflo $t3
	add $t3, $t3, $t0		# t3 = t3 + t0
	
	addi $sp, $sp, -4		#  push pixel value onto Stack
	sw $t3, 0($sp)
	jr $ra



# Game Over

GameOver:
	lw $t0, displayAddress 		# $t0 stores the base address for display
	addi $t1, $zero, 4096
	la $t3, retry
    	add $t2, $zero, $zero           # put the index into $t2
    	
RetryPaint:
	beq $t1, $zero,  UpdatingT1  # loop's if condition
    	add $t5, $t2, $t3    # combine the two components of the address
    	lw $t4, 0($t5)       # get the value from the array cell
    	sw $t4, 0($t0)
    	addi $t2, $t2, 4
    	addi $t1, $t1, -1
    	addi $t0, $t0, 4 		  # next pixel
    	j RetryPaint
    	
UpdatingT1: 
    addi $t1, $zero, 40
    

KeyboardControl:

	lw $t8, 0xffff0000
 	beq $t8,0x1,KeyboardInput1
 
HoldLoop:
 	beq $t1, $zero, ExitCall
 	li $v0, 32
 	li $a0, 100
 	syscall
 	
 	addi $t1, $t1, -1
 	j KeyboardControl
 
ExitCall:
 	j Exit
 	
KeyboardInput1:
	lw $t2, 0xffff0004

	beq $t2, 0x72, respond_to_r
	beq $t2, 0x52, respond_to_r 
	
	j Exit
	
respond_to_r:
	j Start
	

