# LethalCreature Check


LethalArea:

	addi $t0, $s3, -1280
	addi $t7, $t0, 256
	lw $t1, creaturePixel($zero)

# t2 stores 1 st tentacle value (top left)
# t3 stores 2 nd tentacle value (top right)
# t4 stores 3 rd tentacle value (bottom left)
# t5 stores 4 th tentacle value (bottom right)

	addi $t2, $t1, -520
	addi $t3, $t1, -500
	addi $t4, $t1, 760
	addi $t5, $t1, 780
	
	addi $t6, $zero, 4

IllegalAreaCheckLoop:
	beq $t6, $zero, JumpBackToFunction
	beq $t0, $t2, GameOver
	beq $t0, $t3, GameOver
	beq $t0, $t4, GameOver
	beq $t0, $t5, GameOver
	
	beq $t7, $t2, GameOver
	beq $t7, $t3, GameOver
	beq $t7, $t4, GameOver
	beq $t7, $t5, GameOver
	
	addi $t0, $t0, 4
	addi $t7, $t7, 256
	addi $t6, $t6, -1
	j IllegalAreaCheckLoop
	
JumpBackToFunction:
	jr $ra




# BackgroundPaint Start

BackgroundPaintFunction:
	# parameters: None
	# returns: None


	lw $t0, displayAddress 		# $t0 stores the base address for display
	addi $t1, $zero, 4096
	la $t3, backgroundNew
    	add $t2, $zero, $zero           # put the index into $t2
    	
BackgroundLoop:
	beq $t1, $zero, RestOfBackground  # loop's if condition
    	add $t5, $t2, $t3    # combine the two components of the address
    	lw $t4, 0($t5)       # get the value from the array cell
    	sw $t4, 0($t0)
    	addi $t2, $t2, 4
    	addi $t1, $t1, -1
    	addi $t0, $t0, 4 		  # next pixel
    	j BackgroundLoop

	
RestOfBackground:
	jr $ra

