DoodleJump:
	# parameters: None
	# return: None
	
	addi $sp, $sp, -4		#  push ra value onto Stack
	sw $ra, 0($sp)
	
	jal DoodlePlatformJump
	lw $t7, 0($sp)			# pop yes/no value from stack for doodle jump into t7
	addi $sp, $sp, 4
	
	lw $ra, 0($sp)			# pop ra value from stack
	addi $sp, $sp, 4
	
	beq $t7, $zero, jump_up_platform		# if t7 = 0, then jump_up
	
	addi $t8, $zero, 1		# t8 = 5
	bge $a2, $t8, jump_up_platform		# If a2 >= t8, then jump_up
	
jump_down:
	addi $s3, $s3, 256
	jr $ra

jump_up_platform:
	addi $s3, $s3, -512
	addi $a2, $a2, -1
	jr $ra
	
# ---------------------------------------------------------------

DoodlePlatformJump:
	#Parameter: None
	
	# Return:
	# 1. jump yes/no (0/1)
	lw $t0, displayAddress 		# $t0 stores the base address for display
	addi $a1, $s3, 512		# a1 = s3 + 512
	
	add $t1, $s0, $zero		# t1 = s0 (so has the platform address)
	add $t6, $s1, $zero
	add $t7, $s2, $zero
	add $t8, $s4, $zero
	addi $t2, $zero, 15		# loop length
CheckLoop1:
	beq $t2, $zero, NoJump
	beq $a1, $t1, YesJump
	beq $a1, $t6, YesJump
	beq $a1, $t7, YesJump
	beq $a1, $t8, YesJump
	addi $t1, $t1, 4
	addi $t6, $t6, 4
	addi $t7, $t7, 4
	addi $t8, $t8, 4
	addi $t2, $t2, -1
	j CheckLoop1

NoJump:
	addi $t5, $zero, 1
	addi $sp, $sp, -4		#  push t5 value onto Stack
	sw $t5, 0($sp)
	jr $ra

YesJump:
	addi $a2, $zero, 9
	addi $t5, $zero, 0
	addi $sp, $sp, -4		#  push t5 value onto Stack
	sw $t5, 0($sp)
	
	# updating score value
	la $t2, scoreNumber		# load address of the variabe
	lw $t4, scoreNumber($zero)
	addi $t3, $t4, 1
	sw $t3, 0($t2)
	
	jr $ra
	
	
