DoodlePaint:
	# parameters: first pixel for doodle drawing
	# return: void
	
	# s0 to t6
	# s1 to t7
	lw $t6, displayAddress  # $t6 stores the base address for display
	
	lw $t7, 0($sp)		# pop first position of pixel for doodle in t7
	addi $sp, $sp, 4
	
	li $t2, 0xFFA500 	# t2 stores the colour orange
	
	add $t3, $zero, $t7 	# t3 = s3 (first pixel value in t3)
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save $ra value onto stack
	sw $ra, 0($sp)
	
	#function call 1
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	#function call 2
	li $t2, 0xFFA500 	# t2 stores the colour orange
	
	addi $t3, $t7, -256 	# t3 = s1 - 256 (first pixel value in t3)	
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	# function call 3
	
	li $t2, 0x964B00 	# t2 stores the colour brown
	
	addi $t3, $t7, -512 	# t3 = s1 - 512 (first pixel value in t3)	
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	# function call 4
	
	li $t2, 0x964B00 	# t2 stores the colour brown
	
	addi $t3, $t7, -768 	# t3 = s1 - 768 (first pixel value in t3)	
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	# function call 5
	li $t2, 0xFFA500 	# t2 stores the colour orange
	
	addi $t3, $t7, -1024 	# t3 = s1 + 128 (first pixel value in t3)	
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	# drawing the beak
	addi $t3, $t7, -1024 	# t3 = s1 + 128 (first pixel value in t3)
	li $t2, 0xffff00 	# t2 stores the colour orange
	addi $t3, $t3, 20
	sw $t2, 0($t3)
	addi $t3, $t3, 4
	sw $t2, 0($t3)
	addi $t3, $t3, 4
	sw $t2, 0($t3)
	
	# function call 6
	li $t2, 0xFFA500 	# t2 stores the colour orange
	
	addi $t3, $t7, -1280 	# t3 = s1 + 128 (first pixel value in t3)	
	addi $t4, $zero, 4 	# (width - 1) of doodle
	
	addi $sp, $sp, -4	# save colour onto Stack
	sw $t2, 0($sp)
	
	addi $sp, $sp, -4	# save length of line onto Stack
	sw $t4, 0($sp)
	
	addi $sp, $sp, -4	# save first pixel onto Stack
	sw $t3, 0($sp)
	jal LineFunction
	
	
	# drawing legs
	li $t1, 0x9acd32
	addi $t3, $t7, 256		# left upper-leg
	sw $t1, 0($t3)
	addi $t3, $t3, 256		# left lower-leg
	sw $t1, 0($t3)
	addi $t3, $t3, 4
	sw $t1, 0($t3)
	addi $t3, $t3, 4
	sw $t1, 0($t3)
	
	li $t1, 0x9acd32
	addi $t3, $t7, 272		# right upper-leg
	sw $t1, 0($t3)
	addi $t3, $t3, 256		# right lower-leg
	sw $t1, 0($t3)
	addi $t3, $t3, 4
	sw $t1, 0($t3)
	addi $t3, $t3, 4
	sw $t1, 0($t3)
	
	lw $ra, 0($sp)		# pop the $ra value back
	addi $sp, $sp, 4
	
	
	jr $ra

# ---------------------------------------------------------------


LineFunction: 
	# paramenters: first pixel point,  length of line,  colour 
	# return: void
	lw $t0, 0($sp)		# pop out first pixel point value in t0
	addi $sp, $sp, 4
	
	lw $t1, 0($sp)		# pop out length of line in t1
	addi $sp, $sp, 4
	
	lw $t2, 0($sp)		# pop out colour
	addi $sp, $sp, 4
	
	sw $t2, 0($t0)
	
Condition:
	beq $t1, $zero, LineFunctionEnd 	# If t1 == 0, then go to LineFunctionEnd 
	addi $t1, $t1, -1 	# t1 = t1 - 1
	addi $t0, $t0, 4 	# t0 = t0 + 2
	sw $t2, 0($t0) 		# paint the next pixel of the plank
	j Condition

LineFunctionEnd:
	jr $ra
	

