LethalCreaturePaint:
	# Parameter: 
	# 1. first pixel of lethal creature
	# Return: None
	
	lw $t1, 0($sp)		# pop out first pixel point value in t1
	addi $sp, $sp, 4
	add $t3, $zero, $t1	# update first pixel in t3
	
	li $t2, 0xff0000
	sw $t2, 0($t1)
	li $t2, 0x000000
	addi $t3, $t3, 256
	sw $t2, 0($t3)
	addi $t3, $t3, 4
	sw $t2, 0($t3)
	li $t2, 0xff0000
	addi $t3, $t1, 4
	sw $t2, 0($t3)
	
	#top left leg
	li $t2, 0x000000
	addi $t3, $t1, -256
	addi $t3, $t3, -4
	sw $t2, 0($t3)
	addi $t3, $t3, -256
	addi $t3, $t3, -4
	sw $t2, 0($t3)
	
	# top right leg
	li $t2, 0x000000
	addi $t3, $t1, 8
	addi $t3, $t3, -256
	sw $t2, 0($t3)
	addi $t3, $t3, -256
	addi $t3, $t3, 4
	sw $t2, 0($t3)
	
	# botton left leg
	li $t2, 0x000000
	addi $t3, $t1, 512
	addi $t3, $t3, -4
	sw $t2, 0($t3)
	addi $t3, $t3, 256
	addi $t3, $t3, -4
	sw $t2, 0($t3)
	
	# bottom right leg
	li $t2, 0x000000
	addi $t3, $t1, 8
	addi $t3, $t3, 512
	sw $t2, 0($t3)
	addi $t3, $t3, 256
	addi $t3, $t3, 4
	sw $t2, 0($t3)
	
	jr $ra
	
	
	
	
