Score:
	# Parameter: None
	# Return: Null
	lw $t2, scoreNumber($zero)
	
	addi $t3, $zero, 10 		# putting 10 in t3 so that we can obtain ones and tens digit
	div $t2, $t3
	
	mfhi $t3 			# get ones digit
	mflo $t4 			# get tens digit
	
	addi $sp, $sp, -4		# push t4 value to stack
	sw $t4, 0($sp)			# push t3 value to stack
	
	addi $sp, $sp, -4		# push t3 value to stack
	sw $t3, 0($sp)

OnesCall:
	j OnesValue

TensCall:
	j TensValue

RestOfScoreFunction:
	jr $ra
			
OnesValue:
	lw $t3, 0($sp)			# pop ones value from t3 register
	addi $sp, $sp, 4
	
	add $t5, $zero, $zero 		# compare with 0
	beq $t3, $t5, ZeroDrawOnes
			
	addi $t5, $zero, 1 		# compare with 1
	beq $t3, $t5, OneDrawOnes	
			
	addi $t5, $zero, 2 		# compare with 2
